// 如果 Node 在当前目录没找到 tool.js 文件，则会去 node_modules 里面去查找
// var tools2 = require('03_tool-multiply');

// console.log(tools2.multiply(1, 2, 3, 4));
var tools = {
  add: (...numbers) => {
    let sum = 0;
    for (let number in numbers) {
      sum += numbers[number];
    }
    return sum;
  },
  multiply: (...numbers) => {
    let sum = numbers[0];
    for (let number in numbers) {
      sum = sum * numbers[number];
    }
    return sum;
  }
}

module.exports = tools;
